import { coup, couponConstantsonConstants } from "../actions/constants";

const initState = {
  recharges: [],
  loading:false
};

export default (state = initState, action) => {
  switch (action.type) {
      case couponConstants.G:
          state={
              ...state,
              loading:true
          }

    case couponConstants.GET_ALL_RECHARGE_SUCCESS:
      state = {
        ...state,
        recharges: action.payload.recharges,
      };
      break;
      case couponConstants.GET_ALL_RECHARGE_FAILURE:
          state={...state,
        loading:false}
  }

  return state;
};
