import axios from '../helpers/axios'
import {couponConstants} from './constants'




export const addCoupon=(data)=>{

    return async (dispatch) => {
			try {
				dispatch({ type: couponConstants.GET_ALL_RECHARGE_REQUEST });
				const res = await axios.get(`/coupon/addCoupon`,data);
				if (res.status === 201) {
					const { coupon } = res.data;
					dispatch({
						type: couponConstants.ADD_NEW_COUPON_SUCCESS,
						payload: { coupon },
					});
				} else {
					dispatch({ type: couponConstants.ADD_NEW_COUPON_FAILURE });
				}
			} catch (error) {
				console.log(error);
			}
		};

}
