import React,{useState} from 'react'
import MODAL from "../components/UI/Modal";
import { CInput } from "@coreui/react";

export default function Coupons(props) {
	const [addCouponModal, setAddCouponModal] = useState(false);
	const [state, setstate] = useState(initialState)



	const renderAddCouponModal=()=>{
		return (
			<MODAL
				modalTitle='Add New Coupon'
				show={addCouponModal}
				handleClose={() => setAddCouponModal(false)}>
				<CInput
					type={"text"}
					label={"Category Name"}
					value={""}
					placeholder={"Enter Coupon Name"}
					// onChange={(e) => setCategoryName(e.target.value)}
				/>
				<div>
					<label className='m-2'>Coupon type</label>
					<select class='form-select' aria-label='Default select example'>
						<option selected>select type</option>
						<option value='fixed'>Fixed Discount</option>
						<option value='percentage'>Percentage Discount</option>
					</select>
				</div>
				<CInput
					type={"number"}
					label={"Amount."}
					value={""}
					placeholder={"Enter Coupon amount"}
					// onChange={(e) => setCategoryName(e.target.value)}
				/>

				<div className='m-2'>
					<label className='m-2'>Valid From</label>
					<input type='date' />
				</div>

				<div className='m-2'>
					<label className='m-2'>Valid To</label>
					<input type='date' />
				</div>
			</MODAL>
		);
	}








    return (
			<>
				{renderAddCouponModal()}
				<div className='card'>
					<div
						class='card-header d-flex '
						style={{ justifyContent: "space-between" }}>
						<h5>Your Coupons</h5>
						<button type='button' className='btn btn-primary mr-5' onClick={()=>setAddCouponModal(true)}>
							Add Coupon
						</button>
					</div>
					<div class='card-body'>
						<div></div>
					</div>
				</div>
			</>
		);
}
